mapper.select:
select g.ID id , g.MODEL_ID modelId , g.GOODS_ATTRIBUTE_F goodsAttributeF , g.GOODS_ATTRIBUTE_S goodsAttributeS , g.GOODS_NEW_PRICE goodsNewPrice , g.GOODS_OLD_PRICE goodsOldPrice , g.GOODS_INVENTORY goodsInventory , g.CREATE_TIME createTime , g.UPDATE_TIME UpdateTime , g.REMOVE_TIME removeTime from gm_goods g
and g.GOODS_ENABLE = 1
and g.MODEL_ID = #{modelId}
and g.MODEL_ID = #{id}
order by g.CREATE_TIME desc

select g.ID id , g.MODEL_ID modelId , g.GOODS_ATTRIBUTE_F goodsAttributeF , g.GOODS_ATTRIBUTE_S goodsAttributeS , g.GOODS_NEW_PRICE goodsNewPrice , g.GOODS_OLD_PRICE goodsOldPrice , g.GOODS_INVENTORY goodsInventory , g.CREATE_TIME createTime , g.UPDATE_TIME UpdateTime , g.REMOVE_TIME removeTime from gm_goods g
and g.GOODS_ENABLE = 1
and g.MODEL_ID = #{modelId}
and g.GOODS_ATTRIBUTE_F = #{goodsAttributeF}
and g.GOODS_ATTRIBUTE_S = #{goodsAttributeS}
order by g.CREATE_TIME desc

select g.ID id , g.MODEL_ID modelId , g.GOODS_ATTRIBUTE_F goodsAttributeF , g.GOODS_ATTRIBUTE_S goodsAttributeS , g.GOODS_NEW_PRICE goodsNewPrice , g.GOODS_OLD_PRICE goodsOldPrice , g.GOODS_INVENTORY goodsInventory , g.CREATE_TIME createTime , g.UPDATE_TIME UpdateTime , g.REMOVE_TIME removeTime from gm_goods g
and g.GOODS_ENABLE = 1
and g.MODEL_ID = #{modelId}
and g.GOODS_ATTRIBUTE_F = #{goodsAttributeF}
and g.GOODS_ATTRIBUTE_S = #{goodsAttributeS}
order by g.CREATE_TIME desc

mapper.update:
	update 	gm_goods
	SET		GOODS_COUNT = #{count} 
	where 	ID = #{id}
