mapper.select:
select count(*)
from
gm_store

	STORE_ENABLE=1
	 and ID=#{id}
	 and USER_ID=#{userId}
	 and STORE_NAME=#{storeName}

mapper.update:
update gm_store
STORE_NAME=#{storeName}, STATEMENT=#{statement}, EXPRESS_EXPENSES=#{expressExpenses}, DISCOUNT_ID=#{discountId}, STORE_LEVEL=#{storeLevel}
where ID=#{id}

update gm_store
set STORE_ENABLE=0 REMOVE_TIME=NOW()
where ID=#{id}
